declare
    pragma autonomous_transaction;
begin 
    crudo.generate_matrices.crud_table( i_owner => '#OBJECT_OWNER#', i_table_name => '#OBJECT_NAME#');
commit;
exception when others then
    rollback;
    raise;
end;


declare
    pragma autonomous_transaction;
begin
    crudo.generate_matrices.set_override
    ( 
        i_table_owner => '#OBJECT_OWNER#', 
        i_table_name => '#OBJECT_NAME#',
        i_object_owner => '#0#',
        i_object_name => '#1#',
        i_object_type => '#2#',
        i_create => '#3#',                            
        i_read => '#4#',
        i_update => '#5#',
        i_delete => '#6#'
    );
commit;
exception when others then
    rollback;
    raise;
end;


declare
    pragma autonomous_transaction;
begin
    crudo.generate_matrices.remove_override
    ( 
        i_table_owner => '#OBJECT_OWNER#', 
        i_table_name => '#OBJECT_NAME#',
        i_object_owner => '#0#',
        i_object_name => '#1#',
        i_object_type => '#2#'
    );
commit;
exception when others then
    rollback;
    raise;
end;
